// ==== SERVER - START

func Server(ports ...int) {
	for _, port := range ports {
		fmt.Printf("Starting server %d ...\n", port)
		go http.ListenAndServe(":"+strconv.Itoa(port), &handle{port})
	}
}

type handle struct {
	Port int
}

